defmodule WeWhisper.SignatureTest do
  use ExUnit.Case

  import WeWhisper.Signature

  setup do
    {:ok,
      timestamp: "1415979516",
      nonce: "1320562132",
      signature: "096d8cda45e4678ca23460f6b8cd281b3faf1fc3",
      token: "spamtest",
      encrypted: "",
    }
  end

  test "creates signature by Signature type",
    %{timestamp: timestamp, nonce: nonce, signature: signature, token: token, encrypted: encrypted} do
    assert sign(%{token: token, timestamp: timestamp, nonce: nonce, encrypted: encrypted}) == signature
  end

  test "creates signature",
    %{timestamp: timestamp, nonce: nonce, signature: signature, token: token, encrypted: encrypted} do
    assert sign(token, timestamp, nonce, encrypted) == signature
  end
end
